import React, { useState } from "react";
import { render } from "react-dom";
import './Home.css';
import FormInputGroup from "./FormInputGroup";
import Chart from "chartjs";

import { Form, NavLink } from 'react-router-dom';

function CompoundInterest() {
  const context = document.getElementById("data-set").getContext("2d");
let line = new Chart(context, {});
//Values from the form
const intialAmount = document.getElementById("initialamount");
const years = document.getElementById("years");
const rates = document.getElementById("rates");
const compound = document.getElementById("compound");

//Messge
const message = document.getElementById("message");

//The calculate button
const button = document.querySelector(".input-group button");
//Attach an event listener
button.addEventListener("click", calculateGrowth);

const data = [];
const labels = [];

function calculateGrowth(e) {
    e.preventDefault();
    data.length = 0;
    labels.length = 0;
    let growth = 0;
    try {
        const initial = parseInt(intialAmount.value);
        const period = parseInt(years.value);
        const interest = parseInt(rates.value);
        const comp = parseInt(compound.value);

        for(let i = 1; i <= period; i++) {
            const final = initial * Math.pow(1 + ((interest / 100) / comp), comp * i);
            data.push(toDecimal(final, 2));
            labels.push("Year " + i);
            growth = toDecimal(final, 2);
        }
        //
        message.innerText = `You will have this amount ${growth} after ${period} years`;
        drawGraph();
    } catch (error) {
        console.error(error);
    }
}

function drawGraph() {
    line.destroy();
    line = new Chart(context, {
        type: 'line',
        data: {
            labels,
            datasets: [{
                label: "compound",
                data,
                fill: true,
                backgroundColor: "rgba(12, 141, 0, 0.7)",
                borderWidth: 3
            }]
        }
    });
}

function toDecimal(value, decimals) {
    return +value.toFixed(decimals);
}

  return (
    <form onSubmit={(e) => e.preventDefault()}>
       <div class="calculator">
            <div class="heading">
                <h3>Compound Interest Calculator</h3>
                <small>Your money growth over time using compound interest...</small>
            </div>
            <form class="compound-form">
                <div class="input-group">
                    <label for="initialamount">Initial Amount</label>
                    <input type="number" id="initialamount" />
                </div>
                <div class="input-group">
                    <label for="years">Investment Years</label>
                    <input type="number" id="years" />
                </div>
                <div class="input-group">
                    <label for="rates">Estimated Rates(%)</label>
                    <input type="number" id="rates" />
                </div>
                <div class="input-group">
                    <label for="rates">Compound Interval</label>
                    <select id="compound">
                        <option value="1">Annualy</option>
                        <option value="4">Quartely</option>
                        <option value="2">Semiannualy</option>
                        <option value="12">Monthly</option>
                    </select>
                </div>
                <div class="input-group">
                    <button>Calculate</button>
                </div>
            </form>
        </div>
        <div class="results">
            <h3 id="message"></h3>
            <canvas id="data-set"></canvas>
        </div>
        </form>
  );
}


export default CompoundInterest
