import React, { useState } from "react";
import { render } from "react-dom";
import './Home.css';
import FormInputGroup from "./FormInputGroup";

import { Form, NavLink } from 'react-router-dom';

function CompoundInterest() {
  const [principal, setPrincipal] = useState("");
  const [years, setYears] = useState();
  const [interest, setInterest] = useState();
  const [result, setResult] = useState(0);


  function calculateCompoundInterest() {
    function percentageToDecimal(percent) {
      return percent / 12 / 100;
    }
    setResult(
      principal * Math.pow(1 + percentageToDecimal(interest), years )

    )

    return result;
}


  return (
    <form onSubmit={(e) => e.preventDefault()}>
      <FormInputGroup 
      text= "Principal Value"
      placeholder={"Enter value here"}
      value={principal}
      onInput={(e) => setPrincipal(e.target.value)}
      onkeyup={calculateCompoundInterest}/>
      <FormInputGroup 
      text= "Years"
      placeholder={"Enter value here"}
      value={years}
      onInput={(e) => setYears(e.target.value)}
      onkeyup={calculateCompoundInterest}/>
      <FormInputGroup 
      text= "Interest"
      placeholder={"Enter value here"}
      value={interest}
      onInput={(e) => setInterest(e.target.value)}
      onkeyup={calculateCompoundInterest}/>
      <h4>
        Total Amount:
        {parseFloat(result.toFixed(2))} 

      </h4>
      <button
      type = "submit"
      onClick={setResult}>Calculate      </button>
    </form>
  );
};


export default CompoundInterest
