import React, { useState } from "react";
import { render } from "react-dom";
import './Home.css';

import { NavLink } from 'react-router-dom';

const CompoundInterest = () => {
  const [principal, setPrincipal] = useState();
  const [years, setYears] = useState();
  const [interest, setInterest] = useState();
  const [result, setResult] = useState();

  const calculate = () => {
    // A=p(1+(r/n))^(nt)
    const result = principal * Math.pow(1 + interest, years);
    setResult(result.toFixed(2));
  };

  return (
    <>
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          height: "40vh",
        }}
      >
        <form>
          <input
            label="What is the principal?"
            variant="outlined"
            type="number"
            onChange={(e) => setPrincipal(e.target.value)}
          />
          <input
            label="How many years?"
            variant="outlined"
            type="number"
            onChange={(e) => setYears(e.target.value)}
          />
          <input
            label="Annual Interest rate?"
            variant="outlined"
            type="number"
            onChange={(e) => setInterest(e.target.value / 100)}
          />
          <br />
          <br />
          <button
            variant="contained"
            color="primary"
            onClick={() => {
              calculate();
            }}
          >
            Calculate
          </button>
          <br />
          <br />
          <div style={{ fontSize: "30px" }}>${result}</div>
        </form>
      </div>
    </>
  );
};

render(<CompoundInterest />, document.querySelector("#root"));
export default CompoundInterest
